import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchCommunities, joinCommunity } from "../features/community/communitySlice";
import { Link } from "react-router-dom";

const CommunityList = () => {
  const dispatch = useDispatch();
  const { communities, loading, error } = useSelector((state) => state.community);

  useEffect(() => {
    dispatch(fetchCommunities());
  }, [dispatch]);

  const handleJoin = (id) => {
    dispatch(joinCommunity(id));
  };

  return (
    <div className="p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-blue-600">Communities</h1>
        <Link
          to="/communities/create"
          className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
        >
          + Create Community
        </Link>
      </div>

      {loading && <p>Loading...</p>}
      {error && <p className="text-red-500">{error}</p>}

      <div className="grid gap-4">
        {communities.map((c) => (
          <div key={c._id} className="p-4 bg-white rounded-lg shadow flex justify-between items-center">
            <div>
              <h2 className="text-lg font-semibold">{c.name}</h2>
              <p className="text-gray-600">{c.description}</p>
            </div>
            <button
              onClick={() => handleJoin(c._id)}
              className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
            >
              Join
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CommunityList;
