import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchPostsByCommunity, createPost } from "../features/post/postSlice";
import API from "../api/axiosInstance";

const CommunityDetail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { posts, loading, error } = useSelector((state) => state.post);
  const { communities } = useSelector((state) => state.community);

  const [content, setContent] = useState("");
  const [file, setFile] = useState(null);

  // Find community name from Redux
  const community = communities.find((c) => c._id === id);

  useEffect(() => {
    dispatch(fetchPostsByCommunity(id));
  }, [dispatch, id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!content.trim() && !file) return;

    // Use FormData for file upload
    const formData = new FormData();
    formData.append("communityId", id);
    formData.append("content", content);
    if (file) formData.append("media", file);

    try {
      const { data } = await API.post("/posts", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      // add new post manually since thunk expects JSON
      dispatch({ type: "post/create/fulfilled", payload: data });
      setContent("");
      setFile(null);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="p-6">
      {/* ✅ Show community name */}
      <h1 className="text-3xl font-bold text-blue-600 mb-2">
        {community ? community.name : "Community"}
      </h1>
      <p className="text-gray-500 mb-6">
        {community?.description || ""}
      </p>

      {/* Post Form */}
      <form onSubmit={handleSubmit} className="mb-6 space-y-3">
        <textarea
          placeholder="What's on your mind?"
          value={content}
          onChange={(e) => setContent(e.target.value)}
          className="w-full border p-2 rounded"
        />

        <input
          type="file"
          accept="image/*,video/*"
          onChange={(e) => setFile(e.target.files[0])}
          className="block"
        />

        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Post
        </button>
      </form>

      {/* Posts List */}
      {loading && <p>Loading posts...</p>}
      {error && <p className="text-red-500">{error}</p>}

      <div className="space-y-4">
        {posts.length === 0 ? (
          <p>No posts yet. Be the first to post!</p>
        ) : (
          posts.map((post) => (
            <div
              key={post._id}
              className="border rounded p-4 shadow-sm bg-white"
            >
              <p className="text-gray-800 mb-2">{post.content}</p>
              {/* ✅ Show media if exists */}
              {post.mediaUrl && post.mediaType === "image" && (
                <img src={post.mediaUrl} alt="post" className="rounded-lg" />
              )}
              {post.mediaUrl && post.mediaType === "video" && (
                <video
                  src={post.mediaUrl}
                  controls
                  className="rounded-lg w-full"
                />
              )}
              <p className="text-sm text-gray-500">
                by {post.user?.username || "Unknown"}
              </p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default CommunityDetail;
