import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import API from "../../api/axiosInstance";

// Fetch posts by community
export const fetchPostsByCommunity = createAsyncThunk(
  "post/fetchByCommunity",
  async (communityId, { rejectWithValue }) => {
    try {
      const { data } = await API.get(`/posts/community/${communityId}`);
      return data; // array of posts
    } catch (err) {
      return rejectWithValue(err.response?.data?.message || "Failed to fetch posts");
    }
  }
);

// Create new post
export const createPost = createAsyncThunk(
  "post/create",
  async ({ communityId, content }, { rejectWithValue }) => {
    try {
      const { data } = await API.post(`/posts`, { communityId, content });
      return data; // new post
    } catch (err) {
      return rejectWithValue(err.response?.data?.message || "Failed to create post");
    }
  }
);

const postSlice = createSlice({
  name: "post",
  initialState: {
    posts: [],
    loading: false,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      // Fetch posts
      .addCase(fetchPostsByCommunity.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchPostsByCommunity.fulfilled, (state, action) => {
        state.loading = false;
        state.posts = action.payload;
      })
      .addCase(fetchPostsByCommunity.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })
      // Create post
      .addCase(createPost.fulfilled, (state, action) => {
        state.posts.unshift(action.payload); // add new post at top
      });
  },
});

export default postSlice.reducer;
